import React from "react";

class App extends React.Component {
  state = {
    title: "",
    body: "",
  };

  handleChange = (event) => {};

  render() {
    //JSX
    return (
      <div>
        <h2>Welcome to my App</h2>
        <form>
          <div className="form-input">
            <input
              type="text"
              name="title"
              placeholder="Title"
              value={this.state.title}
              onChange={this.handleChange}
            />
          </div>
          <div className="form-input">
            <textarea
              placeholder="Body"
              name="body"
              cols="30"
              rows="10"
              value={this.state.body}
              onChange={this.handleChange}
            ></textarea>
          </div>

          <button>Submit</button>
        </form>
      </div>
    );
  }
}

export default App;
